frappe.ui.form.on("Batch Process", "refresh", function(frm) {
    cur_frm.add_custom_button(__("Submit Batch"), function() {

	frappe.call ({
	    "method": "mobile_fun_customisation.controllers.batch_process.submit_documents",
	    "args": {
		"doc": cur_frm.doc
	    }
        });
    });
});

frappe.ui.form.on("Purchase Order", "validate", function(frm) {

    var dateStart = moment(frappe.defaults.get_user_default("year_start_date"));
    var dateEnd = moment(frappe.defaults.get_user_default("year_end_date"));
    var timeValues = [];
    var today = moment(cur_frm.transaction_date).format("YYYY-MM");

    while (dateEnd > dateStart) {
        timeValues.push(dateStart.format("YYYY-MM"));
        dateStart.add(1, "month");
    }

    for (i = 0; i < timeValues.length; i++) {
        function rowGen(i) {
            var pur = frappe.call({
                method: "frappe.client.get_value",
                args: {
                    "doctype": "Fiscal Period",
                    "filters": {
                        "name": timeValues[i]
                    },
                    "fieldname": [
                        "purchasing"
                    ]
                },
                callback: function(r) {
                    pur = (r.message.purchasing);
                    if (today === timeValues[i] && pur === 1) {
                        frappe.msgprint("The fiscal period you are trying to post into is closed for purchasing entries");
                        frappe.validated = false;
                    }
                }
            })
        }
        rowGen(i);
    }
});

frappe.ui.form.on("Purchase Order", "refresh", function(frm) {
    cur_frm.set_query("batch_id", function() {
        return {
            "filters": {
                "batch_type": cur_frm.doctype
            }
        };
    });
});

frappe.ui.form.on('Purchase Order', 'supplier', function(frm, cdt, cdn){
    frappe.call({
        'method': 'frappe.client.get_value',
        'args': {
            'doctype': 'Supplier',
	    'fieldname': 'creditor_id',
            'filters': {
                'supplier_name': locals[cdt][cdn].supplier
            }
        },
        'callback': function(res){
            frappe.model.set_value(cdt, cdn, 'creditor_id', res.message.creditor_id);
        }
    });
});

frappe.ui.form.on("Fiscal Period Status", {
    fiscal_year: function(frm) {
        var dateStart = moment(frappe.defaults.get_user_default("year_start_date"));
        var dateEnd = moment(frappe.defaults.get_user_default("year_end_date"));
        var timeValues = [];

        while (dateEnd > dateStart) {
            timeValues.push(dateStart.format("YYYY-MM"));
            dateStart.add(1, "month");
        }

        for (i = 0; i < timeValues.length; i++) {
            var new_row = cur_frm.add_child("fiscal_period");
            new_row.fiscal_period = timeValues[i];
	    new_row.purchasing = 1;
	    new_row.sales = 1;
	    new_row.financial = 1;
        }
    }
});

